import javax.swing.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.util.List;import java.util.ArrayList;class Reservation {    private int id;    private String name;    private String date;    private int numberOfGuests;    public Reservation(int id, String name, String date, int numberOfGuests) {        this.id = id;        this.name = name;        this.date = date;        this.numberOfGuests = numberOfGuests;    }    public int getId() {        return id;    }    public String getName() {        return name;    }    public String getDate() {        return date;    }    public int getNumberOfGuests() {        return numberOfGuests;    }}class ReservationSystem {    private List<Reservation> reservations = new ArrayList<>();    private int nextId = 1;    public Reservation makeReservation(String name, String date, int numberOfGuests) {        Reservation reservation = new Reservation(nextId++, name, date, numberOfGuests);        reservations.add(reservation);        return reservation;    }    public List<Reservation> getReservations() {        return reservations;    }    public Reservation getReservationById(int id) {        for (Reservation reservation : reservations) {            if (reservation.getId() == id) {                return reservation;            }        }        return null;    }    public boolean cancelReservation(int id) {        Reservation reservation = getReservationById(id);        if (reservation != null) {            reservations.remove(reservation);            return true;        }        return false;    }}class ReservationSystemUI extends JFrame implements ActionListener {    private ReservationSystem reservationSystem = new ReservationSystem();    private JTextField nameField, dateField, guestsField;    private JTextArea outputArea;    public ReservationSystemUI() {        super("Reservation System");        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        setSize(400, 300);        setLayout(null);        JLabel nameLabel = new JLabel("Name:");        nameLabel.setBounds(20, 20, 80, 20);        add(nameLabel);        nameField = new JTextField();        nameField.setBounds(120, 20, 200, 20);        add(nameField);        JLabel dateLabel = new JLabel("Date:");        dateLabel.setBounds(20, 50, 80, 20);        add(dateLabel);        dateField = new JTextField();        dateField.setBounds(120, 50, 200, 20);        add(dateField);        JLabel guestsLabel = new JLabel("Number of Guests:");        guestsLabel.setBounds(20, 80, 120, 20);        add(guestsLabel);        guestsField = new JTextField();        guestsField.setBounds(140, 80, 50, 20);        add(guestsField);        JButton reserveButton = new JButton("Make Reservation");        reserveButton.setBounds(20, 120, 150, 30);        reserveButton.addActionListener(this);        add(reserveButton);        JButton viewButton = new JButton("View Reservations");        viewButton.setBounds(200, 120, 150, 30);        viewButton.addActionListener(this);        add(viewButton);        outputArea = new JTextArea();        outputArea.setBounds(20, 160, 350, 100);        add(outputArea);    }    public void actionPerformed(ActionEvent e) {        if (e.getActionCommand().equals("Make Reservation")) {            String name = nameField.getText();            String date = dateField.getText();            int numberOfGuests = Integer.parseInt(guestsField.getText());            Reservation reservation = reservationSystem.makeReservation(name, date, numberOfGuests);            outputArea.setText("Reservation made with ID " + reservation.getId());        } else if (e.getActionCommand().equals("View Reservations")) {            StringBuilder sb = new StringBuilder();            sb.append("Reservations:\n");            for (Reservation r : reservationSystem.getReservations()) {                sb.append(r.getId()).append(" - ").append(r.getName()).append(" - ").append(r.getDate()).append(" - ").append(r.getNumberOfGuests()).append("\n");            }            outputArea.setText(sb.toString());        }    }    public static void main(String[] args) {        ReservationSystemUI reservationSystemUI = new ReservationSystemUI();        reservationSystemUI.setVisible(true);    }}